mapper.select:
SELECT * FROM TB_USER
WHERE USE_F = 'Y'
AND BINARY(USER_ID) =
#{userId}
AND BINARY(USER_PWD) = #{userPwd}

SELECT USER_ID
FROM TB_USER
WHERE USER_ID = #{userId}

mapper.update:
UPDATE	TB_USER
SET		USER_NICKNAME = #{userNickname}
WHERE	USER_ID = #{userId}

UPDATE	TB_USER
SET		USER USER_EMAIL = #{userEmail}
WHERE	USER_ID = #{userId}
